import React, { Component, useState, useEffect } from 'react';
import * as yup from 'yup'; 
import axios from 'axios';
import { AppBar, TextField, MuiThemeProvider } from '@material-ui/core';
import { render } from '@testing-library/react';


const formSchema = yup.object().shape({
    username: yup.string()
            .required('Username is a required field.'),
    password: yup.string()
            .required('Password is a required field.')
})

 //state for dislaying user info
 const [data, setData] = useState([]);

 const [buttonDisabled, setButtonDisabled] = useState(true);

 useEffect(() => {
    formSchema.isValid(userInput).then(valid => {
      setButtonDisabled(!valid);
    });
  }, [userInput]);

const [errorState, setErrorState] = useState({
    username: '',
    password: ''
});

class Login extends Component {

    styles = {
        button: {
            margin: 15
        }
    }

    [userInput, setUserInput] = useState({
        username: '',
        password: ''
    });




     validator = e => {
        yup
            .reach(formSchema, e.target.name)
            .validate(e.target.value)
            .then( valid => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: ''
                });
            })
            .catch(err => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: err.errors[0]
                })
            })
    }

     submitHandler = e => {
        e.preventDefault();

        //reset form once submitted
        setUserInput({
            username: '',
            password: ''
        });

        //ensure user data is being returned
        axios
            .post('https://reqres.in/api/users', userInput)
            .then( res => setData(res))
            .catch( err => console.log(err.res))

        console.log('login successful')
    };

     changeHandler = e => {
        e.persist();
        validator(e);

        setUserInput({...userInput, [e.target.name]: e.target.value});
    };

     state = {
        isPasswordShown: false
    }

     togglePasswordVisibility = () => {
        const [isPasswordShown] = state
        this.setState({isPasswordShown : !isPasswordShown})
    }
    render = () => {
        return(
            <div className="form-input">
                <h1 className="app-bar">Log In</h1>
                <p>Log in using your username and password</p>
                <form onSubmit={submitHandler}>

                    <div>
                        <MuiThemeProvider>
                            <TextField
                                type="text"
                                id="username"
                                name="username"
                                value={userInput.username}
                                onChange={changeHandler}
                                placeholder="Username"
                            /><br/>
                            <label htmlFor="username">
                                {/*Username*/}
                                {errorState.username.length > 0 ? (<p className="error">{errorState.username}</p>) : null}
                            </label><br/>
                        </MuiThemeProvider>

                        <MuiThemeProvider>

                            <TextField
                                type={(togglePasswordVisibility) ? "password" : "text"}
                                id="password"
                                name="password"
                                value={userInput.password}
                                onChange={changeHandler}
                                placeholder="Password"
                            /><br/>
                            <i className="fa fa-eye password-icon"
                                onClick={state}
                            ></i>
                            <label htmlFor="password"> {/*Password*/}
                                {errorState.password.length > 0 ? (<p className="error">{errorState.password}</p>) : null}
                            </label><br/>                        
                        </MuiThemeProvider>
                    </div>
                    <button style={styles.button} disabled={buttonDisabled}>Log In</button>
                    <pre>{JSON.stringify(data, null, 2)}</pre>
                </form>
                <p>Don't have an account? <span>Sign Up</span></p>
            </div>
        )
    }
}

export default Login;
