import React, { useState, useEffect } from 'react';
import * as yup from 'yup'; 
import axios from 'axios';
import { Link } from 'react-router-dom';

//comp
import PasswordToggle from './PasswordToggle';

const formSchema = yup.object().shape({
    email: yup.string()
            .required('An email address is required.'),
    username: yup.string()
            .required('Username is a required field.'),
    password: yup.string()
            .required('Password is a required field.')
            .test('len', 'Password must be more than 5 characters long.', val => val.length > 5),
    legal: yup.boolean()
            .oneOf([true], "Must agree to Terms of Use and Privacy Policy.")
})

function Signup(){

    const styles = {
        links: {
            textDecoration: 'none',
            color: '#63B3ED',
            cursor: 'pointer'
        }
    }

    const [passwordInputType, ToggleIcon] = PasswordToggle();

    const [data, setData] = useState([]);

    const [userInput, setUserInput] = useState({
        email: '',
        username:'',
        password: '',
        legal: false
    });

    const [errorState, setErrorState] = useState({
        email: '',
        username: '',
        password: '',
        legal: ''
    });

    const [buttonDisabled, setButtonDisabled] = useState(true);
    useEffect(() => {
        formSchema.isValid(userInput).then(valid => {
          setButtonDisabled(!valid);
        });
    }, [userInput]);

    const validator = (e) => {
        yup
            .reach(formSchema, e.target.name)
            .validate(e.target.value)
            .then(valid => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: ''
                });
            })
            .catch(err => {
                setErrorState({
                    ...errorState,
                    [e.target.name]: err.errors[0]
                });
            });
    };

    const submitHandler = e => {
        e.preventDefault();

        //reset form once submitted
        setUserInput({
            email: '',
            username: '',
            password: '',
            legal: false
        });

        axios
            .post(`https://reqres.in/api/users`, userInput)
            .then( res => setData(res))
            .catch(err => console.log(err.res))
        console.log('login successful')
    };

    const changeHandler = e => {
        e.persist();
        validator(e);
        let value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
        setUserInput({...userInput, [e.target.name]: value});
    };

    return (
        <div className="signup-div">
            <div className="sign-up-sub">
                <div className="form-info">
                    <h1 className="app-bar">Get started with your account</h1>
                    <p>Find your people. Engange your customers. <br/>Build your brand.
                        Do it all with ToDo List.<br/> <p></p>Already have an account? <Link to="/" style={styles.links}>Log in</Link>
                    </p>
                    <div className="form-input">
                        <form onSubmit={submitHandler}>

                            <div>
                                <label htmlFor="email">Email</label><br/>
                                <input
                                        type="text"
                                        id="email"
                                        name="email"
                                        value={userInput.email}
                                        onChange={changeHandler}
                                />
                                {errorState.email.length > 0 ? (<p className="error">{errorState.email}</p>) : null}
                            </div>

                            <div>
                                <label htmlFor="username">Username</label><br/>
                                <input
                                        type="text"
                                        id="username"
                                        name="username"
                                        value={userInput.username}
                                        onChange={changeHandler}
                                />
                                {errorState.username.length > 0 ? (<p className="error">{errorState.username}</p>) : null}
                            </div>

                            <div>
                                <label htmlFor="password">Password</label><br/>
                                <input
                                        type={passwordInputType}
                                        id="password"
                                        name="password"
                                        value={userInput.password}
                                        onChange={changeHandler}
                                />
                                <span className="password-icon">{ToggleIcon}</span>
                                {errorState.password.length > 0 ? (<p className="error">{errorState.password}</p>) : null}
                                {errorState.password > 0 ? (<p className="error">{errorState.password}</p>) : null}
                            </div>
                            <div>
                                <label htmlFor="legal">
                                    <input
                                            type="checkbox"
                                            id="legal"
                                            name="legal"
                                            checked={userInput.legal}
                                            onChange={changeHandler}
                                    />
                                    I agree to the <span style={styles.links}>Terms of Use</span> and <span style={styles.links}>Privacy Policy</span>.

                                </label>
                            </div>
                            <button disabled={buttonDisabled}>Get Started!</button>
                            <pre>{JSON.stringify(data, null, 2)}</pre>
                        </form>
                    </div>
                </div>
            </div>
            <div className="form-graphics">
                {/* <h1>hi im image content</h1> */}
            </div>
        </div>
    )
}

export default Signup;
