import React, { Component, useState, useEffect } from 'react';
import * as yup from 'yup'; 
import axios from 'axios';
import { AppBar, TextField, MuiThemeProvider } from '@material-ui/core';
import { render } from '@testing-library/react';


const formSchema = yup.object().shape({
    username: yup.string()
            .required('Username is a required field.'),
    password: yup.string()
            .required('Password is a required field.')
})


class Login extends Component {

     state = {
        isPasswordShown: false
    }

     togglePasswordVisibility = () => {
        const [isPasswordShown] = this.state
        this.setState({isPasswordShown : !isPasswordShown})
    }
    render = () => {
        const {isPasswordShown} = this.state;

        return(
            <div className="form-input">
                <h1 className="app-bar">Log In</h1>
                <p>Log in using your username and password</p>
                <form onSubmit={submitHandler}>

                    <div>
                        <MuiThemeProvider>
                            <TextField
                                type="text"
                                id="username"
                                name="username"
                                value={userInput.username}
                                onChange={changeHandler}
                                placeholder="Username"
                            /><br/>
                            <label htmlFor="username">
                                {/*Username*/}
                                {errorState.username.length > 0 ? (<p className="error">{errorState.username}</p>) : null}
                            </label><br/>
                        </MuiThemeProvider>

                        <MuiThemeProvider>

                            <TextField
                                type={(isPasswordShown) ? "password" : "text"}
                                id="password"
                                name="password"
                                value={userInput.password}
                                onChange={changeHandler}
                                placeholder="Password"
                            /><br/>
                            <i className="fa fa-eye password-icon"
                                onClick={this.togglePasswordVisibility}
                            ></i>
                            <label htmlFor="password"> {/*Password*/}
                                {errorState.password.length > 0 ? (<p className="error">{errorState.password}</p>) : null}
                            </label><br/>                        
                        </MuiThemeProvider>
                    </div>
                    <button style={styles.button} disabled={buttonDisabled}>Log In</button>
                    <pre>{JSON.stringify(data, null, 2)}</pre>
                </form>
                <p>Don't have an account? <span>Sign Up</span></p>
            </div>
        )
    }
}

export default Login;
